css:
 table {
        width: 100%;
    }
    .width-50{
        width:50%;
    }
    .width-25{
        width: 25%;
    }
    .width-33{
        width: 33%;
    }
    .width-100{
        width: 100%;
    }
    .red{
        color: red;
    }
    .b-bottom{
        border-bottom: 1px solid;
    }
    .center{
        text-align: center;
    }

    @media screen {

  div.divFooter {
    display: none;
  }
  .download-btn {
    position: absolute;
    top: 10px;
    right: 10px
  }
  .print-btn {
    position: absolute;
    top: 10px;
    right: 60px
  }
}
@media print {
  div.divFooter {
    position: fixed;
    bottom: 0;
    text-align:left;
    border-top: 1px solid;
    width: 100%;
    padding-top: 3px;
    display: none;
  }
  div.divFooter img{
    position: absolute;
    width: 80px;
    height: 40px;
    right: 10px;
    top: 5px;
  }
  .header-html {
    position: fixed;
    top: 0;
    
  }
  .download-btn {
    display: none;
  }
  .print-btn {
    display: none;
  }
  tfoot{
      display: table-footer-group !important;
  }

}
.page-break{
    page-break-after: always;
}
@media print {
  tfoot {page-break-after: always;}
}

@page {
  size: A4;
}
.header{
    height: 300px;
}
