client_script:
frappe.web_form.handle_success = () => {
    frappe.msgprint(__('Thank You for applying!'));
    window.location = "/";
    }


var search = window.location.search;
var invoice = search.split("&")[1];
if ( ! invoice){
frappe.msgprint(__('Please enter valid link'));
return location.replace("/");
}
invoice = invoice.split("=")[1];

frappe.call({
method: "erpnext.healthcare.doctype.patient_survey.patient_survey.is_survey_enabled",
args: {
    sales_invoice: invoice
},
callback: (res) => {
console.log(res)
    if (res.message){
        frappe.web_form.set_value("sales_invoice", invoice.split("_")[0]);
    }else{
        frappe.msgprint(__('You have submitted your survey before'));
        return location.replace("/");
    }
}
})
